def matrix_multiply(self, matrix_a, matrix_b):
  zip_b = list(zip(*matrix_b))
  return [[sum(ele_a*ele_b for ele_a, ele_b in zip(row_a, col_b)) 
          for col_b in zip_b] for row_a in matrix_a]
